export const usuarios = [
    { id: 1, nome: 'Fulano', email: 'fulano@teste.com', senha: 'senha123' },
    { id: 2, nome: 'Ciclano', email: 'ciclano@teste.com', senha: 'senha456' },
    { id: 3, nome: 'Beltrano', email: 'beltrano@teste.com', senha: 'senha789' },
  ];

  export function consultarUsuario(email, senha) {
    const index = usuarios.findIndex((usuario) => usuario.email === email && usuario.senha === senha);
    if (index !== -1) {
      return true;
    }else{
        return false;
    }
  }
  
  export function adicionarUsuario(usuario) {
    usuario.id = usuarios.length + 1;
    usuarios.push(usuario);
  }

  export function removerUsuario(id) {
    const index = usuarios.findIndex((usuario) => usuario.id === id);
    if (index !== -1) {
      usuarios.splice(index, 1);
    }
  }
