import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { Button, Avatar, Input  } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';

// import { consultarUsuario } from '../backend/dados.js';
import { firebaseConfig } from '../backend/autentica.js';

// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

import { getAuth, signInWithEmailAndPassword } from "firebase/auth";

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

function HomeScreen({ navigation }) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(false);
  const [errorEmail, setErrorEmail] = useState(null);
  const [errorPassword, setErrorPassword] = useState(null);

  function logar() {
    // Aqui você pode implementar a lógica para cadastrar o usuário
    // console.log(`Email: ${email}, Senha: ${password}`);
    if((email, password) !== ''){
      // if(consultarUsuario(email, password)){
      //   return navigation.navigate('Lista');
      // }else{
      //   alert("Login ou Senha inválidos!")
      // }
      setErrorEmail(null);
      setErrorPassword(null);
      const auth = getAuth();
      signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // login bem sucedido
        const user = userCredential.user;
        alert("Usuário logado: "+ user.email);
        navigation.navigate('Lista');
      })
      .catch((error) => {
        // erro ao fazer login
        alert("Login ou Senha inválidos!");
      });

    }else{
      // alert('Favor inserir Login e Senha!');
      if(email == null){
        setErrorEmail('Preenhca seu e-mail corretamente');
        setError(true);
      }
      if(password == null){
        setErrorPassword('Preenhca sua senha corretamente');
        setError(true);
      }
      return !error;
    }
    
  }

  function cadastrar() {
    // Aqui você pode implementar a lógica para cadastrar o usuário
    // console.log(`opa!!!`);
    ()=>navigation.navigate('Cadastro');
  }

  return (

    <View style={styles.container}>

      <Avatar size="xlarge" rounded
        source={{
          uri: "https://ifmr-sa.org/wp-content/uploads/2022/10/Ze-gotinha.png"
        }} style={styles.avatar} />
        
        <Text style={styles.title}>Vacinação Digital</Text>

      <Input
        style={styles.input}
        placeholder="Login"
        placeholderTextColor="#aaaaaa"
        errorStyle={{ color: 'red' }}
        errorMessage={errorEmail}
        leftIcon={
          <Icon
            name='envelope'
            size={24}
            color='black'
          />
        }
        onChangeText={(text) => setEmail(text)}
        value={email}
        underlineColorAndroid="transparent"
        autoCapitalize="none"
      />
      <Input
        style={styles.input}
        placeholder="Senha"
        placeholderTextColor="#aaaaaa"
        leftIcon={{ type: 'font-awesome', name: 'lock' }}
        errorStyle={{ color: 'red' }}
        errorMessage={errorPassword}
        secureTextEntry={true}
        onChangeText={(text) => setPassword(text)}
        value={password}
        underlineColorAndroid="transparent"
        autoCapitalize="none"
      />
      <TouchableOpacity style={styles.button} onPress={logar}>
        <Text style={styles.buttonText}>Login</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('Cadastro')}>
        <Text style={styles.buttonText} >Cadastrar-se</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ffffff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
  },
  input: {
    width: '80%',
    height: 50,
    backgroundColor: '#f2f2f2',
    margin: 10,
    padding: 8,
    color: '#000000',
    borderRadius: 14,
    fontSize: 18,
  },
  button: {
    backgroundColor: '#1c313a',
    padding: 10,
    margin: 10,
    borderRadius: 14,
    width: '80%',
    alignItems: 'center',
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
  },
  avatar: {
    width: 150,
    height: 150,
    borderRadius: 75,
    borderWidth: 4,
    borderColor: '#1c313a',
    marginBottom: 20,
  },
});

export default HomeScreen;
