import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { Button, Avatar, Input, Overlay } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext.js';
import { getAuth, signInWithEmailAndPassword, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from "firebase/auth";

function InicialScreen({ navigation }) {

  const {user, setUser} = useContext(AuthContext);
  console.log(user);

    // Registrar o observador para monitorar o estado de autenticação
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, handleAuthStateChanged);

  useEffect(() => {
    auth.onAuthStateChanged((user) => {

      if(user){
        const {uid, displayName, photoURL} = user;

        if(!displayName || !photoURL)
          // throw new Error("O usuário não tem displayName ou photoURL!");
          alert("O usuário não tem displayName ou photoURL!");

        setUser({
          id: uid,
          avatar: photoURL,
          name: displayName,
        });
      }

    })
    
  }, []);

  // Função de retorno de chamada que é executada sempre que o estado de autenticação é alterado
  const handleAuthStateChanged = (user) => {
    if (user) {
      // O usuário está autenticado
      console.log("Usuário autenticado:", user);
      // Faça o que for necessário com o usuário autenticado
    } else {
      // O usuário está desconectado
      console.log("Usuário desconectado");
      // Faça o que for necessário quando o usuário estiver desconectado
    }
  };

  return (

    <View style={styles.container}>

      <Avatar size="xlarge" rounded
        source={{
          uri: "https://img.freepik.com/vetores-premium/medico-com-uma-vacina-covid-19-corre-atras-de-uma-bacteria-coronavirus-conceito-de-vacinacao-design-plano-de-ilustracao-vetorial-injecao-em-seringa-ambulancia-com-kit-de-primeiros-socorros-e-vacina-contra-a-gripe_153097-299.jpg?w=2000"
        }} style={styles.avatar} />
        
      <Text style={styles.title}> Olá, {user} ! </Text>

      <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('Home')}>
        <Text style={styles.buttonText} > ENTRAR </Text>
      </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#497e99',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
    color: 'white',
  },
  input: {
    width: '80%',
    height: 50,
    backgroundColor: '#f2f2f2',
    margin: 10,
    padding: 8,
    color: '#000000',
    borderRadius: 14,
    fontSize: 18,
  },
  button: {
    backgroundColor: '#1c313a',
    padding: 10,
    margin: 10,
    borderRadius: 14,
    width: '80%',
    alignItems: 'center',
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
  },
  avatar: {
    width: 150,
    height: 150,
    borderRadius: 75,
    borderWidth: 4,
    borderColor: '#1c313a',
    marginBottom: 20,
  },
});

export default InicialScreen;
