import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { Button, Header, ListItem, Avatar, Icon  } from 'react-native-elements';
import { lista, setarAtual } from '../backend/dados.js';

function ListaScreen({ navigation }) {

  function alterarExcluir(l){
    setarAtual(l);
    navigation.navigate('AlterarContato');
  }

  return (
    <View >
      <Header
        leftComponent={ <Button
          icon={
            <Icon
              name="arrow-left"
              size={25}
              color="white"
            />
          }
          title=""
          onPress={()=>navigation.navigate('Home')}            
        /> }
        rightComponent={<Button
          title="+"
          onPress={() => navigation.navigate('Contato')}
        />}
        centerComponent={{ text: 'Lista de Contatos', style: { color: '#fff', fontSize: 25 } }}
      />
        {
          lista.map((l, i) => (
            <ListItem key={i} bottomDivider onPress={alterarExcluir(l)}>
              <Avatar source={{ uri: l.avatar_url }}  />
              <ListItem.Content>
                <ListItem.Title>{l.name}</ListItem.Title>
                <ListItem.Subtitle>{l.telefone}</ListItem.Subtitle>
              </ListItem.Content>
            </ListItem>
          ))
        }
      </View>
      );
}

      const styles2 = StyleSheet.create({
        container: {
        flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#fff',
      paddingTop: 40
  },
      title: {
        fontSize: 24,
      fontWeight: 'bold',
      marginBottom: 24,
      paddingTop: 40
  },
      input: {
        width: '80%',
      height: 40,
      borderWidth: 1,
      borderColor: '#ccc',
      borderRadius: 4,
      paddingHorizontal: 16,
      marginBottom: 16,
  },
      button: {
        backgroundColor: '#1c313a',
      padding: 10,
      margin: 10,
      borderRadius: 14,
      width: '80%',
      alignItems: 'center',
  },
      buttonText: {
        color: '#fff',
      fontSize: 16,
      fontWeight: 'bold',
  },
});

export default ListaScreen;
