import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { Input } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import { GoogleSignin, statusCodes } from '@react-native-google-signin/google-signin';

import { firebaseConfig } from '../backend/autentica.js';

// Initialize Firebase
const app = initializeApp(firebaseConfig);

function HomeScreen({ navigation }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorEmail, setErrorEmail] = useState('');
  const [errorPassword, setErrorPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const logar = () => {
    if (email !== '' && password !== '' && email !== null && password !== null) {
      setErrorEmail('');
      setErrorPassword('');
      const auth = getAuth();
      signInWithEmailAndPassword(auth, email, password)
        .then((userCredential) => {
          // login bem sucedido
          const user = userCredential.user;
          alert('Usuário logado: ' + user.email);
          navigation.navigate('Lista');
        })
        .catch((error) => {
          // erro ao fazer login
          alert('Login ou Senha inválidos!');
        });
    } else {
      if (email === '') {
        setErrorEmail('Preencha seu e-mail corretamente');
      }
      if (password === '') {
        setErrorPassword('Preencha sua senha corretamente');
      }
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleForgotPassword = () => {
    console.log('Esqueceu a senha?');
  };

  const handleGoogleSignIn = async () => {
    try {
      await GoogleSignin.hasPlayServices();
      const userInfo = await GoogleSignin.signIn();
      console.log('Usuário logado com sucesso:', userInfo);
    } catch (error) {
      if (error.code === statusCodes.SIGN_IN_CANCELLED) {
        console.log('Login com o Google cancelado');
      } else if (error.code === statusCodes.IN_PROGRESS) {
        console.log('Login com o Google em andamento');
      } else if (error.code === statusCodes.PLAY_SERVICES_NOT_AVAILABLE) {
        console.log('Serviços do Google Play indisponíveis ou desatualizados');
      } else {
        console.log('Erro ao fazer login com o Google:', error.message);
      }
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>VACCINE APP</Text>
      <Input
        style={styles.input}
        placeholder="Email"
        placeholderTextColor="#aaaaaa"
        errorStyle={{ color: 'red' }}
        errorMessage={errorEmail}
        leftIcon={<Icon name="envelope" size={24} color="black" />}
        onChangeText={(text) => {
          setEmail(text);
          setErrorEmail(null);
        }}
        value={email}
        underlineColorAndroid="transparent"
        autoCapitalize="none"
      />
      <Input
        style={styles.input}
        placeholder="Senha"
        placeholderTextColor="#aaaaaa"
        leftIcon={{ type: 'font-awesome', name: 'lock' }}
        errorStyle={{ color: 'red' }}
        errorMessage={errorPassword}
        secureTextEntry={!showPassword}
        onChangeText={(text) => {
          setPassword(text);
          setErrorPassword(null);
        }}
        value={password}
        underlineColorAndroid="transparent"
        autoCapitalize="none"
        rightIcon={
          <Icon
            name={showPassword ? 'eye-off' : 'eye'}
            type="feather"
            onPress={toggleShowPassword}
          />
        }
      />
      <TouchableOpacity
        onPress={handleForgotPassword}
        style={styles.forgotPasswordContainer}
      >
        <Text style={styles.forgotPasswordText}>Esqueceu a senha?</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={logar}>
        <Text style={styles.buttonText}>Entrar</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate('Cadastro')}
      >
        <Text style={styles.buttonText}>Criar Conta</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={handleGoogleSignIn} style={styles.googleButton}>
        <Icon name="google" size={20} color="red" style={styles.googleIcon} />
        <Text style={styles.googleButtonText}>Entrar com Google</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#497e99',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
    color: 'white',
  },
  input: {
    width: '80%',
    height: 50,
    backgroundColor: '#f2f2f2',
    margin: 10,
    padding: 8,
    color: '#000000',
    borderRadius: 14,
    fontSize: 18,
  },
  button: {
    backgroundColor: '#1c313a',
    padding: 10,
    margin: 10,
    borderRadius: 14,
    width: '80%',
    alignItems: 'center',
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
  },
  forgotPasswordContainer: {
    alignSelf: 'flex-end',
    paddingRight: 40,
  },
  forgotPasswordText: {
    color: '#1c313a',
    fontSize: 16,
    textDecorationLine: 'underline',
    fontWeight: 'bold',
  },
  googleButton: {
    backgroundColor: '#FFFFFF',
    padding: 10,
    margin: 10,
    borderRadius: 14,
    width: '80%',
    alignItems: 'center',
  },
  googleButtonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  googleIcon: {
    marginRight: 10,
  },
});

export default HomeScreen;
